import React from 'react';
import './App.css';
import $ from 'jquery';

/* constants for current_page in state of App */
const PRODUCTS_LIST_PAGE = 1
const PRODUCT_DETAIL_PAGE = 2
const LOGIN_PAGE_PAGE = 3

class Nav extends React.Component{
  constructor(props) {
    super(props)

    this.state = {
      input_category: "",
      input_searchString: ""
    }

    this.handleInputChange = this.handleInputChange.bind(this)
    this.searchProducts = this.searchProducts.bind(this)
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    this.setState({[name]: value});
    console.log(name)
    console.log(value)
  }

  searchProducts(){
    this.props.getProducts(this.state.input_category,this.state.input_searchString)
  }

  /* render login part HTML (top right) */
  singinHTML(){
    if (this.props.isLogin){
      return (
        <div className='login_link'>
          <p>{this.state.session_info.totalnum} in Cart</p>
          <p>Hello,{this.state.session_info.username} (<a href='/#' onClick={() => this.props.handleSignOut}>Sign out</a>)</p>
        </div>
      )
    }else{
      return (
        <div className='login_link'>
          <a href='/#' onClick={() => this.props.handleSignIn()}>Sign in</a>
        </div>
      )
    }
  }

  render(){
    return (
      <nav>
        <div className="category_link">
          <a href='/#' onClick={() => this.props.getProducts('Phones', '')}>Phones</a>
          <a href='/#' onClick={() => this.props.getProducts('Tablets', '')}>Tablets</a>
          <a href='/#' onClick={() => this.props.getProducts('Laptops', '')}>Laptops</a>
        </div>

        <div className='search_box'>
          <select name="input_category" onChange={this.handleInputChange}>
            <option value="">All</option>
            <option value="Phones">Phones</option>
            <option value="Tablets">Tablets</option>
            <option value="Laptops">Laptops</option>
          </select>
          <input 
            type="text" 
            name="input_searchString" 
            value={this.state.input_searchString} 
            onChange={this.handleInputChange} />
          <button onClick={this.searchProducts}>Search</button>
        </div>
        {this.singinHTML()}

      </nav>
    )
  }
}



/* component for showing product list */
class Product_List extends React.Component{
  constructor(props){
    super(props)
  }

  render(){
    return (
      this.props.product_list.map((product => {
        return (
          <div className='product_object'>
            <img src={"http://localhost:3001/" + product.productImage}></img>
            <a>{product.name}</a>
            <a>{product.price}</a>
          </div>)

      }))
    )
  }
}

/* dynamicly change according to current_page state */
class Content extends React.Component {
  constructor(props){
    super(props)
  }
  render(){
    if (this.props.current_page == PRODUCTS_LIST_PAGE){
      return <Product_List product_list={this.props.product_list}/>
    }
    return ""
  }
}

function Footer(){

}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLogin: false,
      current_page: PRODUCTS_LIST_PAGE,
      product_list: [],
      product_info: [],
      session_info: []
    }
    this.getProducts = this.getProducts.bind(this)
    this.getSessionInfo = this.getSessionInfo.bind(this)
    this.handleSignIn = this.handleSignIn.bind(this)
    this.handleSignOut = this.handleSignOut.bind(this)

    this.loadInitialPage();
  }


  /* change product list in state */
  getProducts(category, searchString){
    /* note that the sorting is done in server side */
    console.log('called getProducts()')
    $.ajax({
      type: "GET",
      url: "http://localhost:3001/loadpage?category=" + category + "&searchstring=" + searchString,
      xhrFields: {withCredentials: true},
      dataType: 'JSON',
      success: function(result){
        //change page to products list page and update product_list
        console.log(result)
        this.setState({current_page: PRODUCTS_LIST_PAGE, product_list: result})
      }.bind(this)
    })
  }

  /* change session info in state */
  getSessionInfo(){
    $.ajax({
      type: "GET",
      url: "http://localhost:3001/getsessioninfo",
      xhrFields: {withCredentials: true},
      dataType: 'JSON',
      success: function(result){
        this.setState({session_info: result})
      }.bind(this)
    })
  }

  /* initialize page */
  loadInitialPage(){
    this.getProducts("","");
    this.getSessionInfo();
  }

  handleSignIn(){

  }

  handleSignOut(){

  }

  render(){
    return (
      <React.Fragment>
        <Nav 
          isLogin={this.state.isLogin}
          getProducts={this.getProducts}
          session_info={this.state.session_info}
          handleSignIn={this.handleSignIn}
          handleSignOut={this.handleSignOut}
          />
        <Content 
          current_page={this.state.current_page}
          product_list={this.state.product_list}
          />
        <Footer />
      </React.Fragment>
    )
  }
}

export default App;
