import React from 'react';
import './App.css';
import $ from 'jquery';

function Nav(props){
  return (
    <nav>
      <a href='' onClick={() => props.getProducts('Phones', '')}>Phones</a>
      <a href='' onClick={() => props.getProducts('Tablets', '')}>Tablets</a>

    </nav>
  )
}

class Content extends React.Component {
  constructor(props){
    super(props)
  }
  render(){
    return ""
  }
}

function Footer(){

}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLogin: false,
      currnet_page: 'products',    //'products','detail','login'
      product_list: [],
      product_info: [],
      session_info: []
    }
    this.getProducts = this.getProducts.bind(this)
    this.getSessionInfo = this.getSessionInfo.bind(this)

    this.loadInitialPage();
  }


  /* change product list in state */
  getProducts(category, searchString){
    /* note that the sorting is done in server side */
    console.log('called getProducts()')
    $.ajax({
      type: "GET",
      url: "http://localhost:3001/loadpage?category=" + category + "&searchstring=" + searchString,
      xhrFields: {withCredentials: true},
      dataType: 'JSON',
      success: function(result){
        //change page to products list page and update product_list
        console.log(result)
        this.setState({currnet_page: 'products', product_list: result})
      }.bind(this)
    })
  }

  /* change session info in state */
  getSessionInfo(){
    $.ajax({
      type: "GET",
      url: "http://localhost:3001/getsessioninfo",
      xhrFields: {withCredentials: true},
      dataType: 'JSON',
      success: function(result){
        this.setState({session_info: result})
      }.bind(this)
    })
  }

  /* initialize page */
  loadInitialPage(){
    this.getProducts("","");
    this.getSessionInfo();
  }

  render(){
    return (
      <React.Fragment>
        <Nav 
          isLogin={this.state.isLogin}
          getProducts={this.getProducts}/>
        <Content />
        <Footer />
      </React.Fragment>
    )
  }
}

export default App;
