    console.log('handleUpdateCart() called');
    $.ajax({
      type:"PUT",
      data: {productid: productId, quantity: quantity},
      url: "http://localhost:3001/updatecart",
      dataType: 'JSON',
      xhrFields: {withCredentials: true},
      success: function(result){
        if (result.msg != 'Success'){
